import react, { useState, useEffect } from "react";
import {
  Table,
  TableHead,
  TableCell,
  Paper,
  TableRow,
  TableBody,
  Button,
  styled,
} from "@mui/material";
import { getUsers, deleteUser } from "../../Service/api";
import ResourceCard from "../ResourceCard/ResourceCard";
import axios from "axios";
import { useHistory } from "react-router-dom";
import styles from "./AllUsers.module.css";
import SubResouceDialog from "../SubResouceDialog/SubResouceDialog";
const AllUsers = () => {
  const history = useHistory();
  const [users, setUsers] = useState([]);
  const [imagesLink, setImagesLinks] = useState([]);
  useEffect(() => {
    getAllUsers();
    // getImages();
  }, []);
  const [showModal, setShowModal] = useState(false);
  const deleteUserData = async (id) => {
    await deleteUser(id);
    getAllUsers();
  };
  function openModal() {
    setShowModal((prev) => !prev);
  }
  // function CloseModal() {
  //   console.log("I came in closeModal");
  // }
  const getAllUsers = async () => {
    console.log("getAllUsers");
    let response = await getUsers();
    // console.log("getAllUsers response ", response);

    setUsers(response.data);
  };
  // const URL =
  //   "https://api.unsplash.com/search/photos?page=1&&per_page=10&query=office&client_id=L1-B1K0i6IQSGC4v7KNQmDCovA-KOgHWJZjUTodM_mY";
  // const getImages = async () => {
  //   const response = await axios.get(URL);
  //   setImagesLinks(response);
  //   console.log("getImages", response.data.results);
  // };
  // console.log("response", response);
  // return response.data;
  // };
  return (
    <>
      <div onClick={openModal} className={styles.card}>
        <div className="ResourceCardsWrapper">
          {users.map((post, i) => (
            <div className="ResourceCard" key={i}>
              {console.log(post)}
              <ResourceCard
                imageLink={
                  "data:image/png;base64,"
                }
                _id={post._id}
                Heading={post.Heading}
                Category={post.Category}
                Content={post.Content}
              />
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default AllUsers;
