import axios from "axios";

// const usersUrl = 'http://localhost:3003/users';
const usersUrl = "http://localhost:5500";

export const getUsers = async () => {
  console.log("getUsers called in api js");
  let response = await axios.get(`${usersUrl}/api/all`);
  console.log("response", response);
  return response.data;
};
export const addUser = async (user) => {
  return await axios.post(`${usersUrl}/api/add`, user);
};

export const deleteUser = async (id) => {
  return await axios.delete(`${usersUrl}/api/${id}`);
};

export const editUser = async (id, user) => {
  return await axios.put(`${usersUrl}/api/${id}`, user);
};
