import React, { useState } from "react";
import Card from "@mui/material/Card";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import { Button, styled } from "@mui/material";
import SubResouceDialog from "../SubResouceDialog/SubResouceDialog";
const ResourceCard = ({ _id, imageLink, Heading, Content }) => {
  const [showModal, setShowModal] = useState(false);
  function openModal() {
    setShowModal((prev) => !prev);
  }
  const history = useHistory();
  return (
    <div onClick={openModal} className="cursorP">
      {showModal && <SubResouceDialog postId={_id} />}
      <div className="PostCardWrapper">
        <div className={styles.card}>
          <h3 className={styles.topic}>{room.topic}</h3>
          <div
            className={`${styles.speakers} ${
              room.speakers.length === 1 ? styles.singleSpeaker : ""
            }`}
          >
            <div className={styles.avatars}>
              {room.speakers.map((speaker) => (
                <img
                  key={speaker.id}
                  src={speaker.avatar}
                  alt="speaker-avatar"
                />
              ))}
            </div>
            <div className={styles.names}>
              {room.speakers.map((speaker) => (
                <div key={speaker.id} className={styles.nameWrapper}>
                  <span>{speaker.name}</span>
                  <img src="/images/chat-bubble.png" alt="chat-bubble" />
                </div>
              ))}
            </div>
          </div>
          <div className={styles.peopleCount}>
            <span>{room.totalPeople}</span>
            <img src="/images/user-icon.png" alt="user-icon" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResourceCard;
