import react, { useEffect, useState } from "react";
import {
  FormGroup,
  FormControl,
  InputLabel,
  Input,
  styled,
  Typography,
} from "@mui/material";

// import { addUser } from "../../Service/api";
// import { useNavigate } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { addUser } from "../../Service/api";
import { useSelector } from "react-redux";
import TextInput from "../shared/TextInput/TextInput";
import TextArea from "../shared/TextArea/TextArea";
import Card from "../shared/Card/Card";
import styles from "./AddUser.module.css";
import Button from "../shared/Button/Button";
const initialValue = {
  Heading: "",
  Content: "",
  Category: "",
  CreatorId: "",
  CreatorName: "",
};

const Container = styled(FormGroup)`
    text-color:"red";
    width: 50%;
    margin: 5% 0 0 25%;
    & > div {
        margin-top: 20px;
`;

const AddUser = () => {
  const history = useHistory();
  const [data, setData] = useState(initialValue);
  let { Heading, Content, Category, CreatorId, CreatorName } = data;
  const { user } = useSelector((state) => state.auth);
  const onValueChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  const obj = {
    Heading: Heading,
    Content: Content,
    Category: Category,
    CreatorName: user.name,
    CreatorId: user.id,
  };
  let allFilled = true;
  const addUserDetails = async () => {
    if (Heading == "" || Content == "" || Category == " ") {
      allFilled = false;
      window.alert("Kindly Fill all required fields");
    } else {
      await addUser(obj);
    }
  };
  const redirect = () => {
    if (allFilled) {
      history.push(`/all`);
    } else {
      history.push("/add");
    }
  };

  return (
    <>
      <Card title="Create a fantastic article!!" icon="win">
        <label for="Heading">Heading</label>
        <TextInput
          onChange={(e) => onValueChange(e)}
          name="Heading"
          value={Heading}
          id="my-input"
        />
        <label for="Content">Content</label>
        <TextArea
          rows="10"
          col="0"
          onChange={(e) => onValueChange(e)}
          name="Content"
          value={Content}
          id="my-input"
        />
        <label for="Category">Category</label>
        <TextInput
          onChange={(e) => onValueChange(e)}
          name="Category"
          value={Category}
          id="my-input"
        />
        <label for="CreatorName">CreatorName</label>
        <TextInput name="CreatorName" value={user?.name} id="my-input" />
        <label for="CreatorId">CreatorId</label>
        <TextInput name="CreatorId" value={user?.id} id="my-input" />
      </Card>

      <div className={styles.gap}>
        <Button
          variant="contained"
          color="primary"
          onClick={function (event) {
            addUserDetails();
            redirect();
          }}
        >
          Add User
        </Button>
      </div>
    </>
  );
};

export default AddUser;
