import react, { useEffect, useState } from "react";
import {
  FormGroup,
  FormControl,
  InputLabel,
  Input,
  Button,
  styled,
  Typography,
} from "@mui/material";
// import { addUser } from "../../Service/api";
// import { useNavigate } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { addUser } from "../../http";
import { useSelector } from "react-redux";
const initialValue = {
  Heading: "",
  Content: "",
  Category: "",
};

const Container = styled(FormGroup)`
    text-color:"red";
    width: 50%;
    margin: 5% 0 0 25%;
    & > div {
        margin-top: 20px;
`;

const AddUser = () => {
  let Creator = "";
  let ownerName = "";
  const history = useHistory();
  const [data, setData] = useState(initialValue);
  let { Heading, Content, Category } = data;
  const { user } = useSelector((state) => state.auth);
  console.log("user", user);
  const onValueChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };
  useEffect(() => {
    setData({ ...data, ["Creator"]: user.id });
  }, []);
  const addUserDetails = async () => {
    console.log("addUserCalled", data);
    await addUser(data);
  };
  const redirect = () => {
    history.push(`/all`);
  };

  return (
    <Container>
      <Typography variant="h4">Add Post</Typography>
      <FormControl>
        <InputLabel htmlFor="my-input">Heading</InputLabel>
        <Input
          onChange={(e) => onValueChange(e)}
          name="Heading"
          value={Heading}
          id="my-input"
        />
      </FormControl>
      <FormControl>
        <InputLabel htmlFor="my-input">Content</InputLabel>
        <Input
          onChange={(e) => onValueChange(e)}
          name="Content"
          value={Content}
          id="my-input"
        />
      </FormControl>
      <FormControl>
        <InputLabel htmlFor="my-input">Category</InputLabel>
        <Input
          onChange={(e) => onValueChange(e)}
          name="Category"
          value={Category}
          id="my-input"
        />
      </FormControl>

      <FormControl>
        <Button
          variant="contained"
          color="primary"
          onClick={function (event) {
            addUserDetails();
            redirect();
          }}
        >
          Add User
        </Button>
      </FormControl>
    </Container>
  );
};

export default AddUser;
