import react, { useEffect, useState } from "react";
import {
  FormGroup,
  FormControl,
  InputLabel,
  Input,
  Button,
  styled,
  Typography,
} from "@mui/material";

// import { addUser } from "../../Service/api";
// import { useNavigate } from "react-router-dom";
import { useHistory } from "react-router-dom";
import { addUser } from "../../Service/api";
import { useSelector } from "react-redux";
import TextInput from "../shared/TextInput/TextInput";
import Card from "../shared/Card/Card";
import styles from "./AllUsers.module.css";
const initialValue = {
  Heading: "",
  Content: "",
  Category: "",
  CreatorId: "",
  CreatorName: "",
};

const Container = styled(FormGroup)`
    text-color:"red";
    width: 50%;
    margin: 5% 0 0 25%;
    & > div {
        margin-top: 20px;
`;

const AddUser = () => {
  const history = useHistory();
  const [data, setData] = useState(initialValue);
  let { Heading, Content, Category, CreatorId, CreatorName } = data;
  const { user } = useSelector((state) => state.auth);
  const onValueChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  const obj = {
    Heading: Heading,
    Content: Content,
    Category: Category,
    CreatorName: user.name,
    CreatorId: user.id,
  };
  const addUserDetails = async () => {
    await addUser(obj);
  };
  const redirect = () => {
    history.push(`/all`);
  };

  return (
    <>
      <Card title="Whatâ€™s your full name?" icon="goggle-emoji">
        <label for="html">Heading</label>

        <TextInput
          onChange={(e) => onValueChange(e)}
          name="Heading"
          value={Heading}
          id="my-input"
        />
        <textarea
          onChange={(e) => onValueChange(e)}
          name="Content"
          value={Content}
          id="my-input"
        />
        <TextInput
          onChange={(e) => onValueChange(e)}
          name="Category"
          value={Category}
          id="my-input"
        />
        <TextInput name="CreatorName" value={user?.name} id="my-input" />
        <TextInput name="CreatorId" value={user?.id} id="my-input" />
        <div>
          <Button text="Add User" />
        </div>
      </Card>

      <Button
        variant="contained"
        color="primary"
        onClick={function (event) {
          addUserDetails();
          redirect();
        }}
      >
        Add User
      </Button>
    </>
  );
};

export default AddUser;
