import react, { useState, useEffect } from "react";
import { getUsers } from "../../Service/api";
import ResourceCard from "../ResourceCard/ResourceCard";
import { useHistory } from "react-router-dom";
import styles from "./AllUsers.module.css";
import SubResouceDialog from "../SubResouceDialog/SubResouceDialog";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Button from "../shared/Button/Button";
import axios from "axios";
const AllUsers = ({ isError }) => {
  const history = useHistory();
  const [users, setUsers] = useState([]);
  const [imagesLink, setImagesLinks] = useState([]);
  useEffect(() => {
    getAllUsers();
    getImages();
  }, []);
  const AddArticle = () => {
    history.push("/addArticle");
  };

  const getAllUsers = async () => {
    let response = await getUsers();

    setUsers(response.data);
  };
  var link;
  const URL =
    "https://api.unsplash.com/search/photos?page=1&&per_page=10&query=office&client_id=L1-B1K0i6IQSGC4v7KNQmDCovA-KOgHWJZjUTodM_mY";
  const getImages = async () => {
    const response = await axios.get(URL);
    link = response.data.results[0]["urls"]["full"];
  };

  return (
    <>
      <div className="ResourceCardsWrapper">
        {users.map((post, i) => (
          <div className="ResourceCard" key={i}>
            <ResourceCard
              imageLink={imagesLink[i]["urls"]["full"]}
              _id={post._id}
              Heading={post.Heading}
              Category={post.Category}
              Content={post.Content}
              CreatorId={post.CreatorId}
              CreatorName={post.CreatorName}
            />
          </div>
        ))}
      </div>
      <div className={styles.Add}>
        <Button text="+" onClick={AddArticle}></Button>
      </div>
      <ToastContainer />
    </>
  );
};

export default AllUsers;
