import { useState, useEffect } from "react";

import { useParams } from "react-router-dom";
import { getUser, editUser } from "../../Service/api";
import { useHistory } from "react-router-dom";
import { useSelector } from "react-redux";
import styles from "./EditUser";
import Button from "../shared/Button/Button";
const initialValue = {
  Heading: "",
  Content: "",
  Category: "",
  CreatorId: "",
  CreatorName: "",
};

const EditUser = () => {
  const history = useHistory();
  const [data, setData] = useState(initialValue);
  let { Heading, Content, Category, CreatorId, CreatorName } = data;
  const { id } = useParams();
  const { user } = useSelector((state) => state.auth);
  const onValueChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };
  const obj = {
    Heading: Heading,
    Content: Content,
    Category: Category,
    CreatorName: user.name,
    CreatorId: user.id,
  };
  useEffect(() => {
    loadUserDetails();
  }, []);

  const loadUserDetails = async () => {
    const response = await getUser(id);
    console.log(response);
    setData(response.data);
  };

  let allFilled = true;
  const editUserDetails = async () => {
    if (Heading == "" || Content == "" || Category == " ") {
      allFilled = false;
      window.alert("Kindly Fill all required fields");
    } else {
      const response = await editUser(id, data);
    }
  };
  const redirect = () => {
    if (allFilled) {
      history.push(`/all`);
    } else {
      history.push("/add");
    }
  };

  return (
    <>
      <Card title="Create a fantastic article!!" icon="win">
        <label for="Heading">Heading</label>
        <TextInput
          onChange={(e) => onValueChange(e)}
          name="Heading"
          value={Heading}
          id="my-input"
        />
        <label for="Content">Content</label>
        <TextArea
          rows="10"
          col="0"
          onChange={(e) => onValueChange(e)}
          name="Content"
          value={Content}
          id="my-input"
        />
        <label for="Category">Category</label>
        <TextInput
          onChange={(e) => onValueChange(e)}
          name="Category"
          value={Category}
          id="my-input"
        />
        <label for="CreatorName">CreatorName</label>
        <TextInput name="CreatorName" value={user?.name} id="my-input" />
        <label for="CreatorId">CreatorId</label>
        <TextInput name="CreatorId" value={user?.id} id="my-input" />
      </Card>

      <div className={styles.gap}>
        <Button
          variant="contained"
          color="primary"
          onClick={function (event) {
            addUserDetails();
            redirect();
          }}
        >
          Add User
        </Button>
      </div>
    </>
  );
};

export default EditUser;
